mapper.select:
select
count(moveId)
from movie
where moveName like #{key}
or director like #{key}

select m.*,p.picId,picurl,t.typeid,t.typename
from movie m left join pic p on m.picId = p.picId left join type t on m.typeid = t.typeid
where moveName like #{key}
or director like #{key}
limit #{start},#{offset}
